mapper.select:
select

from album
where album_name like #{albumName, jdbcType=VARCHAR}

mapper.insert:
insert into album (album_id, artist_id, publish_time, 
  album_name, introduction, album_pic_url, 
  sub_count, comment_count, share_count
  )
values (#{albumId,jdbcType=VARCHAR}, #{artistId,jdbcType=VARCHAR}, #{publishTime,jdbcType=TIMESTAMP}, 
  #{albumName,jdbcType=VARCHAR}, #{introduction,jdbcType=VARCHAR}, #{albumPicUrl,jdbcType=VARCHAR}, 
  #{subCount,jdbcType=INTEGER}, #{commentCount,jdbcType=INTEGER}, #{shareCount,jdbcType=INTEGER}
  )
